import React, {useState, useEffect} from "react";

import "./PojectPage.css";
import ContentHeading from "../../shared/Heading/ContentHeading";
import { useParams } from "react-router-dom";

const ProjectPage = props => {
    const [project, setProject] = useState({});
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState();
    const { projectId } = useParams();

    useEffect(() => {
        setIsLoading(true);
        

        fetch(`"http://localhost:5000/projects/${ projectId }"`)
        .then(response => response.json())
        .then(response => {

             console.log(response);
             setProject(response);
        
        })
        .catch(() => {});

    }, []);

    

    return (
        <React.Fragment>
            <ContentHeading className="heading-container">
                {projectId}
            </ContentHeading>
            <div className="container">
            <iframe style={{margin: "auto"}} width="100%" height="515" src="https://www.youtube.com/embed/bvK-Li6tAEo" frameBorder="5" allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture" allowFullScreen></iframe>
            </div>
        </React.Fragment>
    );
};


export default ProjectPage;
