const months = [];

months[0] = "January";
months[1] = "February";
months[2] = "March";
months[3] = "April";
months[4] = "May";
months[5] = "June";
months[6] = "July";
months[7] = "August";
months[8] = "September";
months[9] = "October";
months[10] = "November";
months[11] = "December";

//returns date in "January, 2020" format
export const dateFormatted = (date) => {
    let month = months[date.getMonth()];
    let year = date.getFullYear();
    return `${month}, ${year}`
};
