import React from "react";

import "./Message.css";

const Message = props => {
    return (
        <div className={props.className}>
            <div className={`message-header ${props.msgHeaderClassName}`}><h2>{props.title}</h2></div>
            <div className="message-body"><p>{props.children}</p></div>
        </div>
    );
};

export default Message;
