import React, { useEffect, useState } from "react";
import Card from "../../shared/components/Card";

import "./ProjectsPage.css";
import ContentHeading from "../../shared/Heading/ContentHeading";
import Container from "../../shared/components/Container";
// import snakeGameImg from "../../images/snake_game.png";
// import connect4GameImg from "../../images/Connect4_game.png";
// import pizzeriaImg from "../../images/pizzeria.png";
// import portfolioWSImg from "../../images/portfolio_ws.png";
// import projects from "./Projects.js";



const ProjectsPage = () => {
    const [projects, setProjects ] = useState([]);
    const [isLoading, setIsLoading] = useState(false);

    useEffect(() => {
        setIsLoading(true);

        fetch("http://localhost:5000/projects")
        .then(response => response.json())
        .then((response) => setProjects(() => {
            const data = response;
            
            let groups = [];   //grouping of 3 cards
            let group = [];    //group of 3 cards to be displayed in a single row
            let i;

            for(i = 1; i <= data.length; i++){
                 if((i % 3) !== 0){
                    group.push(data[i - 1]);
                }else{
                    group.push(data[i - 1]);
                    groups.push(group);
                    group = [];
                }
            }
            //push the group of less than 3 elements
            if(group.length !== 0){
                groups.push(group);
            }
        
            return groups;
            }));
    }, []);
    setIsLoading(false);

    return (
        <React.Fragment>
        {}
            <ContentHeading className="heading-container">
                Projects
            </ContentHeading>
            <Container className="container">

                
                { projects.map((gr, index) => {
                    return <div className="row" key={index}>
                            {gr.map((proj)=> {
                                console.log("P1 " + proj);
                             return <div className="col col-12 col-md-6 col-lg-4" key={proj._id}>
                                <Card  className="project-card" title={proj.name} tech={proj.tech} src={ proj.image } alt={`${proj.name} screenshot`} ></Card>
                                </div>
                            })}
                       </div> 
                }) }
            </Container>
            
        </React.Fragment>
    );
};

export default ProjectsPage;
