import React, {useState, useEffect} from "react";

import "./PojectPage.css";
import ContentHeading from "../../shared/Heading/ContentHeading";
import { useParams } from "react-router-dom";
import Message from "../../shared/components/Message";

const ProjectPage = props => {
    const [project, setProject] = useState({});
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState();
    const { projectId } = useParams();

    useEffect(() => {
        setIsLoading(true);
        
        fetch(process.env.REACT_APP_BACKEND_URL + "/projects/" + projectId)
        .then(response => response.json())
        .then(response => {


        setProject(response);
        setIsLoading(false);
        })
        .catch((err) => {
            setIsLoading(false);
            setError(err.message);
        });

    }, []);

    const OkBtnClickHandler = (value, msgType) => {
        //closes message window depending on what type of message it is
        setError(value); 
    }

    return (
        <React.Fragment>
            {isLoading && (<p className="centered">Loading Project...</p>)}
            {error && <Message className="centered error message" msgHeaderClassName="error" title="Error" onOkButtonClick={OkBtnClickHandler}>{error}</Message>}
            <ContentHeading className="heading-container">
                {project.name}
            </ContentHeading>
            <div className="container">
            <iframe style={{margin: "auto"}} width="100%" height="515" src={project.video} frameBorder="5" allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture" allowFullScreen></iframe>
            </div>
        </React.Fragment>
    );
};


export default ProjectPage;
