import React from "react";
import Card from "../../shared/components/Card";

import "./ProjectsPage.css";
import ContentHeading from "../../shared/Heading/ContentHeading";
import Container from "../../shared/components/Container";
import snakeGameImg from "../../images/snake_game.png";
import connect4GameImg from "../../images/Connect4_game.png";
import pizzeriaImg from "../../images/pizzeria.png";
import portfolioWSImg from "../../images/portfolio_ws.png";
import projects from "./Projects.js";



const ProjectsPage = () => {
    
    const groupsOfThree = () => {
        let groups = [];   //grouping of 3 cards
        let group = [];    //group of 3 cards to be displayed in a single row
        let i;

        for(i = 1; i <= projects.length; i++){
            if((i % 3) !== 0){
                group.push(projects[i - 1]);
            }else{
                group.push(projects[i - 1]);
                groups.push(group);
                group = [];
            }
            
        }
        //push the group of less than 3 elements
        if(group.length !== 0){
            groups.push(group);
        }
        
        return groups;
    };
    const DUMMY_PROJ = groupsOfThree();
    
    console.log(groupsOfThree());

    return (
        <React.Fragment>
            <ContentHeading className="heading-container">
                Projects
            </ContentHeading>
            <Container className="container">

                
                { DUMMY_PROJ.map((gr, index) => {
                    return <div className="row" key={index}>
                            {gr.map((proj)=> {
                                console.log("P1 " + proj);
                             return <div className="col col-12 col-md-6 col-lg-4" key={proj.id}>
                                <Card  className="project-card" title={proj.name} tech={proj.tech} src={ import(`${proj.image}`)} alt={`${proj.name} screenshot`} ></Card>
                                </div>
                            })}
                       </div> 
                }) }



                {/* <Container className="row">
                    <Container className="col col-12 col-md-6 col-lg-4" >
                        <Card className="project-card" title="Snake Game" tech={"Android Studio, Java, SQLite"} src={ snakeGameImg } alt="snake game screenshot" ></Card>
                    </Container>
                    <Container className="col col-12 col-md-6 col-lg-4">
                        <Card className="project-card" title="Connect4 Game" tech={"Java, JavaFX, JUnit"} src={ connect4GameImg } alt="connect4 game screenshot"></Card>
                    </Container>
                    <Container className="col col-12 col-md-6 col-lg-4">
                        <Card className="project-card" title="Pizzeria" tech={"Java, Swing, PostgreSQL"} src={ pizzeriaImg } alt="pizzeria screenshot"></Card>
                    </Container>
                </Container>
                <Container className="row">
                    <Container className="col col-12 col-md-6 col-lg-4">
                        <Card className="project-card" title="Portfolio Website" tech={"React.js, jQuery, Bootstrap, Node.js, MongoDB"} src={portfolioWSImg} alt="portfolio website screenshot"></Card>
                    </Container>
                    
                </Container> */}
            </Container>
            
        </React.Fragment>
    );
};

export default ProjectsPage;
