import React from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEnvelopeOpen, faPhone, faEnvelope } from "@fortawesome/free-solid-svg-icons";

import "./ContactMeForm.css";

const ContactMeForm = props => {
    return (
        <div className="container form-container">
            <div className="form-icon-container">
                <FontAwesomeIcon id="form-icon" icon={ faEnvelopeOpen } size="3x" color="lightBlue" />
            </div>
            <div className="contact-info-container">
                <div className="contact-info-icons-container">
                    <FontAwesomeIcon  icon={ faEnvelope } size="2x" />
                    <FontAwesomeIcon  icon={ faPhone } size="2x"/>
                </div>
                <div className="contact-info-text-container">
                    <a href="mailto:dsladychenko@gmail.com">dsladychenko@gmail.com</a>
                    <a href="tel:1-678-899-0093">(678)899-0093</a>
                </div>
            </div>
            <form  action="mailto:dsladychenko@gmail.com">
                <button className="btn btn-primary btn-block" type="submit">Send</button>
            </form>
        </div>
    );
};

export default ContactMeForm;
